mapper.select:
select * from ky_refund_application
where 1 = 1

    and `user_id` = #{userId}


    and `refund_id` = #{refundId}


    and `order_id` = #{orderId}

select a.refund_price, a.create_time, a.refund_code, a.handle_time, a.is_agree, d.title, e.url, g.reason_name from ky_refund_application a left join ky_order_detail b on a.order_id=b.order_id left join ky_product_sku c on b.sku_id=c.sku_id left join ky_product_info d on c.product_id=d.product_id left join ky_product_picture e on c.product_id=e.relation_id left join ky_refurn_reason g on a.reason_id=g.reason_id left join ky_product_spec_value specv ON specv.id IN (c.spec_value_ids) where a.deleted=0 and d.deleted=0 and c.deleted=0 and e.type=1 and e.is_main=1
and `user_id` = #{userId}
and `refund_id` = #{refundId}
and `order_id` = #{orderId}

select d.title, e.url, g.after_sale_type from ky_refund_application a left join ky_order_detail b on a.order_id=b.order_id left join ky_product_sku c on b.sku_id=c.sku_id left join ky_product_info d on c.product_id=d.product_id left join ky_product_picture e on c.product_id=e.relation_id left join ky_refurn_reason g on a.reason_id=g.reason_id left join ky_product_spec_value specv ON specv.id IN (c.spec_value_ids) where a.deleted=0 and d.deleted=0 and c.deleted=0 and e.type=1 and e.is_main=1
and `user_id` = #{userId}
and `refund_id` = #{refundId}
and `order_id` = #{orderId}

mapper.update:
update ky_refund_application
set deleted  = #{afterSale1.deleted},
    modified_time = #{afterSale1.updateTime}
where user_id = #{afterSale1.userId}
  and refund_id=#{afterSale1.refundId}
  and order_id=#{afterSale1.OrderId}
