mapper.select:
SELECT
COUNT(o.order_id)
FROM
ky_order_info o

    LEFT JOIN ky_order_detail d ON d.order_id = o.order_id

WHERE
o.deleted = 0

    AND o.store_id = #{storeId}


    AND o.user_id = #{userId}


    AND o.order_status = #{orderStatus}


    AND o.review_status = #{reviewStatus}


    AND DATE_FORMAT(o.create_time,'%Y-%m-%d') >= #{dateStart}


    AND DATE_FORMAT(o.create_time,'%Y-%m-%d') <= #{dateEnd}


    AND (o.order_code = #{keyword}
    OR d.product_title LIKE CONCAT('%', #{keyword}, '%'))

SELECT o.order_id orderId, o.order_code orderCode, o.store_id storeId, s.store_name storeName, o.user_id userId, u.user_name userName, o.order_status orderStatus, o.total_price totalPrice, o.freight_price freightPrice, o.pay_price payPrice, o.source_type sourceType, o.create_time createTime FROM ky_order_info o LEFT JOIN ky_store_info s ON s.store_id = o.store_id LEFT JOIN ky_user_info u ON u.id = o.user_id
LEFT JOIN ky_order_detail d ON d.order_id = o.order_id
WHERE o.deleted = 0
AND o.store_id = #{storeId}
AND o.user_id = #{userId}
AND o.order_status = #{orderStatus}
AND o.review_status = #{reviewStatus}
AND DATE_FORMAT(o.create_time,'%Y-%m-%d') >= #{dateStart}
AND DATE_FORMAT(o.create_time,'%Y-%m-%d') <= #{dateEnd}
AND (o.order_code = #{keyword} OR d.product_title LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY o.create_time DESC LIMIT #{pageStart},#{pageSize}
